import React, {useEffect,useState} from 'react'
import axios from 'axios'
import './Coinlist.css'
import {FiArrowUpRight,FiArrowDown} from 'react-icons/fi'


const Coinlist = () => {

  const [data,setData] = useState(null)
  const url = 'https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&order=market_cap_desc&per_page=6&page=1&sparkline=false'
  useEffect(() => {
    axios.get(url).then((response) => {
        setData(response.data)
    }).catch((error) => {
        console.log(error)
    })
}, [])

  console.log(data)
if(!data) return null


  return (
    <div className = 'coinlist'>
      <div className = 'coin-container'>
        <div className = 'coin-row'>
        <div className = 'coin'>
          <img src = {data[0].image} alt = 'crypto' />
          <h1>{data[0].name}</h1>
          <p className = 'coin-symbol'>{data[0].symbol}</p>
        </div>
        <div className = 'coin-data'>
          <p className = 'coin-price'>${data[0].current_price}</p>
          <p className = 'coin-volume'>${data[0].total_volume.toLocaleString()}</p>
          <p className = 'coin-change'>{data[0].price_change_percentage_24h}</p>
        </div>
        </div>
      </div>
      </div>
  )
}

export default Coinlist
